import{_ as s,C as o,c as n,o as d,a4 as t,G as r}from"./chunks/framework.C_GGjjHE.js";const m=JSON.parse('{"title":"一.1.网络体系结构","description":"","frontmatter":{"lastUpdated":"Last Updated"},"headers":[],"relativePath":"views/study/ne/theory/一/1.网络体系结构.md","filePath":"views/study/ne/theory/一/1.网络体系结构.md","lastUpdated":1752226003000}'),l={name:"views/study/ne/theory/一/1.网络体系结构.md"};function _(i,a,h,p,c,b){const e=o("ImageViewer");return d(),n("div",null,[a[0]||(a[0]=t('<h1 id="一-1-网络体系结构" tabindex="-1">一.1.网络体系结构 <a class="header-anchor" href="#一-1-网络体系结构" aria-label="Permalink to &quot;一.1.网络体系结构&quot;">​</a></h1><h3 id="_1-网络协议" tabindex="-1">1.网络协议 <a class="header-anchor" href="#_1-网络协议" aria-label="Permalink to &quot;1.网络协议&quot;">​</a></h3><h4 id="定义" tabindex="-1">定义 <a class="header-anchor" href="#定义" aria-label="Permalink to &quot;定义&quot;">​</a></h4><p>网络协议(netword protocol)是为进行网络中的数据交换而建立的<span class="brown">规则、标准或约定</span>。</p><h4 id="协议三要素" tabindex="-1">协议三要素 <a class="header-anchor" href="#协议三要素" aria-label="Permalink to &quot;协议三要素&quot;">​</a></h4><ul><li><span style="color:#07F64A;">语法</span>，即数据与控制信息的结构或者格式。例如：地址字段多长以及它在整个分组中的什么位置。</li><li><span style="color:#07F64A;">语义</span>，即各个控制信息的具体含义，包括需要发出何种控制信息，完成何种动作以及做出何种响应。</li><li><span style="color:#07F64A;">同步(时序)</span>，即事件实现顺序和时间的详细说明，包括数据应该在何时发送出去以及数据应该以什么速率发送。</li></ul><h3 id="_2-计算机网络体系结构" tabindex="-1">2.计算机网络体系结构 <a class="header-anchor" href="#_2-计算机网络体系结构" aria-label="Permalink to &quot;2.计算机网络体系结构&quot;">​</a></h3>',7)),r(e,{src:"/blog/images/study/ne/计算机网络体系结构.png",title:"计算机网络体系结构图"}),a[1]||(a[1]=t('<h3 id="_3-各层协议的主要功能-五层体系结构" tabindex="-1">3.各层协议的主要功能(五层体系结构) <a class="header-anchor" href="#_3-各层协议的主要功能-五层体系结构" aria-label="Permalink to &quot;3.各层协议的主要功能(五层体系结构)&quot;">​</a></h3><p><span class="brown">应用层：</span>如何通过应用进程间的交互来完成特定的网络应用。<br><span class="brown">运输层：</span>负责向两台主机中进程之间的通信提供通用的数据传输服务。<br><span class="brown">网络层：</span>负责为分组交换网上的不同主机提供通信服务。<br><span class="brown">数据链路层：</span>将分组从链路的一端传送到另一端。<br><span class="brown">物理层：</span>在传输媒体上传送比特流。<br></p><h3 id="_4-网络通信的实现过程" tabindex="-1">4.网络通信的实现过程 <a class="header-anchor" href="#_4-网络通信的实现过程" aria-label="Permalink to &quot;4.网络通信的实现过程&quot;">​</a></h3><p>自上向下封装数据，自下向上解封数据</p>',4)),r(e,{src:"/blog/images/study/ne/主机间数据发送过程.png",title:"主机间数据发送过程"}),a[2]||(a[2]=t('<h3 id="_5-每层对应信息的名称" tabindex="-1">5.每层对应信息的名称 <a class="header-anchor" href="#_5-每层对应信息的名称" aria-label="Permalink to &quot;5.每层对应信息的名称&quot;">​</a></h3><table tabindex="0"><thead><tr><th>层</th><th>对应信息</th></tr></thead><tbody><tr><td>应用层</td><td>数据/信息/报文</td></tr><tr><td>传输层</td><td>数据段(TCP)/数据报(UDP)</td></tr><tr><td>网络层</td><td>数据包/报文分组</td></tr><tr><td>数据链路层</td><td>数据帧(传输数据的基本单位)</td></tr><tr><td>物理层</td><td>比特流</td></tr></tbody></table>',2))])}const T=s(l,[["render",_]]);export{m as __pageData,T as default};
