import{_ as r,C as p,c as t,o,a4 as n,G as i,j as a,a as e}from"./chunks/framework.C_GGjjHE.js";const T=JSON.parse('{"title":"一.2.数据通信基础","description":"","frontmatter":{},"headers":[],"relativePath":"views/study/ne/theory/一/2.数据通信基础.md","filePath":"views/study/ne/theory/一/2.数据通信基础.md","lastUpdated":1752226003000}'),u={name:"views/study/ne/theory/一/2.数据通信基础.md"};function _(d,l,b,c,g,h){const s=p("ImageViewer");return o(),t("div",null,[l[0]||(l[0]=n('<h1 id="一-2-数据通信基础" tabindex="-1">一.2.数据通信基础 <a class="header-anchor" href="#一-2-数据通信基础" aria-label="Permalink to &quot;一.2.数据通信基础&quot;">​</a></h1><h3 id="_1-几个常用术语" tabindex="-1">1.几个常用术语 <a class="header-anchor" href="#_1-几个常用术语" aria-label="Permalink to &quot;1.几个常用术语&quot;">​</a></h3><p><span class="brown">数据(data)</span>：承载信息的实体。<br></p><p><span class="brown">信号(signal)</span>：数据的电气或电磁的表现。<br></p><ul><li><span class="brown">模拟信号(analogous signal)</span>：代表消息的参数的取值是<span class="brown">连续的</span>。<br></li><li><span class="brown">数字信号(digital signal)</span>：代表消息的参数的取值是<span class="brown">离散的</span>。<br></li></ul><p><span class="brown">码元(code)</span>：在使用时间域(时域)的波形表示数字信号时，代表不同离散数值的基波形。<br></p>',6)),i(s,{src:"/blog/images/study/ne/信号.png",title:"信号图"}),l[1]||(l[1]=n('<h3 id="_2-编码与调制" tabindex="-1">2.编码与调制 <a class="header-anchor" href="#_2-编码与调制" aria-label="Permalink to &quot;2.编码与调制&quot;">​</a></h3><ul><li>要利用信道传输数据，必须将数据转换为能在传输媒体上传送的信号。</li><li>信道可以分成传送模拟信号的<span class="green">模拟信道</span>和传送数字信号的<span class="green">数字信道</span>两大类。</li><li>数字数据转换成数字信号的过程成为<span class="green">编码(coding)</span>，反之则为<span class="brown">解码(decoding)</span>。</li><li>数字数据转换成模拟信号的过程成为<span class="green">调制(Modulation)</span>，反之则为<span class="brown">解调(Demodulation)</span>。</li></ul>',2)),i(s,{src:"/blog/images/study/ne/编码与调制.png",title:"编码与调制"}),l[2]||(l[2]=a("h3",{id:"_3-常用编码方式",tabindex:"-1"},[e("3.常用编码方式 "),a("a",{class:"header-anchor",href:"#_3-常用编码方式","aria-label":'Permalink to "3.常用编码方式"'},"​")],-1)),l[3]||(l[3]=a("ul",null,[a("li",null,"归零制：正脉冲代表1，负脉冲代表0。"),a("li",null,"不归零制：正电平代表1，负电平代表0。"),a("li",null,"曼切斯特编码：每个码元的中间时刻电平发生跳变，上跳代表0，下跳代表1。"),a("li",null,"差分曼切斯特编码：每个码元的中间时刻电平与前一个码元的电平不同，代表1，相同代表0。"),a("li",null,"单极性编码：只有一个电平，电平代表1。"),a("li",null,"双极性编码：有两个电平，一个代表0，另一个代表1。")],-1)),i(s,{src:"/blog/images/study/ne/常用编码方式.png",title:"常用编码方式"}),l[4]||(l[4]=n('<p><span class="red">曼彻斯特及差分曼彻斯特编码具有自同步能力</span><br></p><p><span class="brown">传统以太网采用曼彻斯特编码</span><br><span class="brown">令牌环网采用差分曼彻斯特编码</span><br></p><h3 id="_4-基本调制方法" tabindex="-1">4.基本调制方法 <a class="header-anchor" href="#_4-基本调制方法" aria-label="Permalink to &quot;4.基本调制方法&quot;">​</a></h3><ul><li><span class="green">调幅(AM)</span>：载波的振幅随基带数字信号而变化，即<span class="brown">幅移键控ASK(Amplitude-Shift Keying)</span>。</li><li><span class="green">调频(FM)</span>：载波的频率随基带数字信号而变化，即<span class="brown">频移键控FSK(Frequency-Shift Keying)</span>。</li><li><span class="green">调相(PM)</span>：载波的初始相位随基带数字信号而变化，即<span class="brown">相移键控PSK(Phase-Shift Keying)</span>。</li></ul>',4)),i(s,{src:"/blog/images/study/ne/基本调制方法.png",title:"基本调制方法"}),l[5]||(l[5]=n('<h3 id="_5-常见调制方式" tabindex="-1">5.常见调制方式 <a class="header-anchor" href="#_5-常见调制方式" aria-label="Permalink to &quot;5.常见调制方式&quot;">​</a></h3><ul><li><span class="green">BPKS</span>：二进制相移键控，2种状态，表示1位bit。</li><li><span class="green">QPSK</span>：正交相移键控，4种状态，表示2位bit。</li><li><span class="green">QAM</span>：正交幅度调制，是相位和幅度调制的组合。</li><li><ul><li>16-QAM：16种状态，表示4位bit。</li></ul></li><li><ul><li>32QAM：32种状态，表示5位bit。</li></ul></li><li><ul><li>64-QAM：64种状态，表示6位bit。</li></ul></li><li><ul><li>256-QAM：256种状态，表示8位bit。</li></ul></li><li><ul><li>1024-QAM：1024种状态，表示10位bit。</li></ul></li></ul><h3 id="_6-信道的极限容量-奈奎斯特准则" tabindex="-1">6.信道的极限容量-奈奎斯特准则 <a class="header-anchor" href="#_6-信道的极限容量-奈奎斯特准则" aria-label="Permalink to &quot;6.信道的极限容量-奈奎斯特准则&quot;">​</a></h3><p><span class="green">奈奎斯特准则</span>：理想低通信道的最高码元传输速率(波特率) = 2W波特<br></p><ul><li>W是理想低通信道的带宽，单位为赫(Hz)</li><li>波特，码元传输速率的单位，即码元/秒</li></ul><div class="formula"> 码元传输的<span class="key">最高</span>速率 = 2W (码元/秒) </div><p>信道的最大传输速率Rmax可表达为</p>',7)),i(s,{src:"/blog/images/study/ne/奈奎斯特准则.png",title:"奈奎斯特准则"}),l[6]||(l[6]=n('<ul><li>W为信道的带宽(以Hz为单位)</li><li>M为码元的状态</li></ul><h3 id="_7-信道的极限传输速率-香农公式" tabindex="-1">7.信道的极限传输速率-香农公式 <a class="header-anchor" href="#_7-信道的极限传输速率-香农公式" aria-label="Permalink to &quot;7.信道的极限传输速率-香农公式&quot;">​</a></h3><p>1948年，香农用信息论的理论推导出了带宽受限且有高斯白噪声干扰的信道的<span class="green">极限、无差错的信息传输速率</span><br></p><p>信道的极限信息传输速率C可表示为：</p><div class="formula"> C = Wlog2(1 + S/N) </div><br> - W为信道的带宽(单位为Hz)<br> - S为信道内所传信号的平均功率<br> - N为信道内部的高斯噪声功率<br> - 香农公式的单位是：bit/Hz<br><br><div class="formula"> 信噪比(dB) = 10log10(S/N)(dB) </div><br><h3 id="_8-奈奎斯特采样定理" tabindex="-1">8.奈奎斯特采样定理 <a class="header-anchor" href="#_8-奈奎斯特采样定理" aria-label="Permalink to &quot;8.奈奎斯特采样定理&quot;">​</a></h3><p>也称为香农 - 奈奎斯特采样定理，是数字信号处理中的核心理论之一，用于描述连续时间信号（模拟信号）与离散时间信号（数字信号）之间的转换关系，明确了无失真采样的基本条件。<br></p><p>对于一个<span class="blod">带宽受限</span>的连续时间信号x，其最高频率成分为fmax(即信号的带宽B = fmax)，若以<span class="blod">采样频率fs</span>对其进行均匀采样，且满足fs ≥ 2fmax则采样后的离散信号可以<span class="blod">完全无失真地恢复出原始连续信号</span><br></p><ul><li>fmax是信号的最高频率(单位：Hz)</li><li>fs是采样频率(单位：Hz)，即每秒采样的次数</li><li>2fmax被称为<span class="blod">奈奎斯特频率</span>，是无失真采样的最低要求</li></ul><h3 id="_9-传输方式" tabindex="-1">9.传输方式 <a class="header-anchor" href="#_9-传输方式" aria-label="Permalink to &quot;9.传输方式&quot;">​</a></h3><ul><li><span class="green">并行传输</span>：是指一次发送n个比特而不是一个比特，为此，在发送端和接收端之间需要有n条传输路线</li><li><ul><li><span class="brown">特点：速度快、距离近、主机内传输</span></li></ul></li><li><ul><li>示例：CPU/主板/外设</li></ul></li><li><span class="green">串行传输</span>：是指数据是一个比特一个比特依次发送的，因此在发送端和接收端之间只需要一条传输线路即可</li><li><ul><li><span class="brown">特点：速度慢、距离远、主机间传输</span></li></ul></li><li><ul><li>示例：主机之间的传输</li></ul></li></ul>',23)),i(s,{src:"/blog/images/study/ne/信道数据传输方式.png",title:"信道数据传输方式"}),l[7]||(l[7]=n('<ul><li><span class="green">异步传输</span>：以字节为独立的传输单位，字节之间的时间间隔不是固定的，接收端尽在每个字节的起始处对字节内的比特实现同步</li><li><ul><li>示例：短信聊天</li></ul></li><li><span class="green">同步传输</span>：以稳定的比特流的形式传输，要采取技术使收发双方的时钟保持同步。例如收发双方接收同一时钟源的时钟信号来实现收发双方的同步，或采用具有自同步能力的曼彻斯特编码等。</li><li><ul><li>示例：视频电话</li></ul></li><li><span class="green">单工通信(单向通信)</span>：只有一个方向的通信而没有反方向的交互，只需要一条信道</li><li><ul><li>示例：广播、电视</li></ul></li><li><span class="green">半双工通信(双向交替通信)</span>：通信的双方都可以发送或接收信息，但不能同时发送和接收信息。需要两条信道</li><li><ul><li>示例：对讲机</li></ul></li><li><span class="green">全双工通信(双向同时通信)</span>：通信的双方可以同时发送和接收信息。需要两条信道</li><li><ul><li>示例：打电话</li></ul></li></ul>',1)),i(s,{src:"/blog/images/study/ne/单双工通信.png",title:"单双工通信"}),l[8]||(l[8]=a("h3",{id:"_10-现实常见编码列表",tabindex:"-1"},[e("10.现实常见编码列表 "),a("a",{class:"header-anchor",href:"#_10-现实常见编码列表","aria-label":'Permalink to "10.现实常见编码列表"'},"​")],-1)),i(s,{src:"/blog/images/study/ne/现实常见编码列表.png",title:"现实常见编码列表"})])}const f=r(u,[["render",_]]);export{T as __pageData,f as default};
