import{_ as r,C as t,c as l,o,b1 as e,G as n,j as i}from"./chunks/framework.BbzeyoJx.js";const b=JSON.parse('{"title":"一.5.数字传输系统","description":"","frontmatter":{},"headers":[],"relativePath":"views/study/ne/theory/一/5.数字传输系统.md","filePath":"views/study/ne/theory/一/5.数字传输系统.md","lastUpdated":1752484449000}'),p={name:"views/study/ne/theory/一/5.数字传输系统.md"};function _(h,a,c,d,T,u){const s=t("ImageViewer");return o(),l("div",null,[a[0]||(a[0]=e('<h1 id="一-5-数字传输系统" tabindex="-1">一.5.数字传输系统 <a class="header-anchor" href="#一-5-数字传输系统" aria-label="Permalink to &quot;一.5.数字传输系统&quot;">​</a></h1><h3 id="_1-早期数字传输系统-电话网" tabindex="-1">1.早期数字传输系统(电话网) <a class="header-anchor" href="#_1-早期数字传输系统-电话网" aria-label="Permalink to &quot;1.早期数字传输系统(电话网)&quot;">​</a></h3><ul><li><span class="green">北美和日本 T1(1.544 Mbit/s)：</span>T1共有24个话路，每个话路采样脉冲用7bit编码，然后再加上1位信令码元，因此一个话路占用8bit。在总体加1个比特作为帧同步码，这样每帧共有193(8*24+1)bit，每秒有<span class="green">8K</span>个T1的帧通过接口，即8K * 193 = 1.544Mbps。</li><li><span class="green">欧洲和中国 E1(2.048 Mbit/s)：</span>一个E1的帧分为32个时隙，一个时隙为8bit，帧长为256比特。每秒有<span class="green">8K</span>个E1的帧通过接口，即8K * 256 = 2.048Mbps。</li></ul>',3)),n(s,{src:"/blog/images/study/ne/电话网传输系统.png",title:"电话网传输系统"}),a[1]||(a[1]=e('<br><h3 id="_2-同步光纤网sonet-synchronous-optical-network" tabindex="-1">2.同步光纤网SONET(Synchronous Optical Network) <a class="header-anchor" href="#_2-同步光纤网sonet-synchronous-optical-network" aria-label="Permalink to &quot;2.同步光纤网SONET(Synchronous Optical Network)&quot;">​</a></h3><p>美国国家标准化组织(ANSI)颁布的美国标准版本，采用时分复用技术。 SONET使用光纤进行数据传输，其中光的波长范围在1310nm至1550nm之间。这种传输方式使得SONET在长距离通信中表现优越。</p><ul><li><span class="green">只能在同步模式下传输数据</span></li><li><span class="green">传输开销总数为9*3 = 27字节</span><br></li></ul><p>标准STS-1帧是9行，每行90字节。每行的前三个字母代表节和线路开销，单个SONET帧以125um(即每秒8000帧)的速度传输。 <span class="brown">8000fps * 90B * 9 * 8 = 51.84 Mbs</span></p><h3 id="_3-同步数字系列sdh-synchronous-digital-hierarchy" tabindex="-1">3.同步数字系列SDH(Synchronous Digital Hierarchy) <a class="header-anchor" href="#_3-同步数字系列sdh-synchronous-digital-hierarchy" aria-label="Permalink to &quot;3.同步数字系列SDH(Synchronous Digital Hierarchy)&quot;">​</a></h3><p>ITU-T以SONET为基础制定的国际标准，采用时分复用技术。 SDH具有强大的容错性，能够在网络中快速检测和恢复故障，提高了系统的稳定性和可靠性。<br></p><ul><li><span class="green">SDH可以在同步模式和异步模式下传输</span></li><li><span class="green">传输开销总数为9*9=81字节</span><br></li></ul><p>对于STM-1，单个SDH帧也以125um传输，帧长是9行，每行270字节。<span class="brown">8000fps * 270B * 9 * 8 = 155.52 Mbs</span>，每行有9字节报头，9字节报头包含复用器和再生器开销。</p><h3 id="_4-sonet与sdh的对应关系" tabindex="-1">4.SONET与SDH的对应关系 <a class="header-anchor" href="#_4-sonet与sdh的对应关系" aria-label="Permalink to &quot;4.SONET与SDH的对应关系&quot;">​</a></h3>',10)),n(s,{src:"/blog/images/study/ne/SONET与SDH的对应关系.png",title:"SONET与SDH的对应关系"}),a[2]||(a[2]=e('<br><h3 id="_5-sonet-sdh标准的意义" tabindex="-1">5.SONET/SDH标准的意义 <a class="header-anchor" href="#_5-sonet-sdh标准的意义" aria-label="Permalink to &quot;5.SONET/SDH标准的意义&quot;">​</a></h3><ul><li>定义了标准光信号，规定了波长为1310nm和1550nm的激光源。</li><li>在物理层定义了帧结构。</li><li>使北美、日本和欧洲这三个地区三种不听的数字传输体制在STM-1等级上获得了统一。</li><li>已成为公认的传输网体制。</li></ul><h3 id="_6-新一代数字传输网络" tabindex="-1">6.新一代数字传输网络 <a class="header-anchor" href="#_6-新一代数字传输网络" aria-label="Permalink to &quot;6.新一代数字传输网络&quot;">​</a></h3><ul><li><span class="green">光传送网OTN(Optical Transport Network)</span>作为向全光网络进行演讲的过渡技术，以光<span class="brown">波分复用</span>技术为基础，直接在光域上对不听波长的信号实现交换和复用，是<span class="brown">SDH</span>和<span class="brown">WDM</span>技术的结合。</li><li><span class="green">分组传送网PTN(Packet Transport Network)</span>旨在提供一种高带宽、低时延、灵活可扩展的传输网络，以支持IP业务的高效传输。它采用分组交换技术，将数据业务封装成IP数据包进行传输，并通过MPLS(多协议标签交换)等技术实现数据包的快速转发和路径优化。</li></ul><h3 id="_7-otn-ptn联合组网架构" tabindex="-1">7.OTN+PTN联合组网架构 <a class="header-anchor" href="#_7-otn-ptn联合组网架构" aria-label="Permalink to &quot;7.OTN+PTN联合组网架构&quot;">​</a></h3>',6)),n(s,{src:"/blog/images/study/ne/OTN+PTN联合组网架构.png",title:"OTN+PTN联合组网架构"}),a[3]||(a[3]=i("br",null,null,-1))])}const N=r(p,[["render",_]]);export{b as __pageData,N as default};
