import{_ as r,C as t,c as o,o as p,b1 as n,G as e,j as a,a as s}from"./chunks/framework.w2ptJf3g.js";const g=JSON.parse('{"title":"一.6.互联网接入技术","description":"","frontmatter":{},"headers":[],"relativePath":"views/study/ne/theory/一/6.互联网接入技术.md","filePath":"views/study/ne/theory/一/6.互联网接入技术.md","lastUpdated":1752484449000}'),u={name:"views/study/ne/theory/一/6.互联网接入技术.md"};function T(b,l,d,_,h,c){const i=t("ImageViewer");return p(),o("div",null,[l[0]||(l[0]=n('<h1 id="一-6-互联网接入技术" tabindex="-1">一.6.互联网接入技术 <a class="header-anchor" href="#一-6-互联网接入技术" aria-label="Permalink to &quot;一.6.互联网接入技术&quot;">​</a></h1><h3 id="_1-互联网接入技术" tabindex="-1">1.互联网接入技术 <a class="header-anchor" href="#_1-互联网接入技术" aria-label="Permalink to &quot;1.互联网接入技术&quot;">​</a></h3><p>互联网接入技术解决的就是最终用户接入本地ISP &quot;<span class="green">最后一公里</span>&quot; 的问题。通常，将用户设备(或用户驻地网络)连接到ISP边缘路由器的物理链路及相关设备的集合称为<span class="green">接入网AN(access network)</span>。<br> 接入网涉及的用户数量巨大，在网络投资中占比很大，是各大运营竞相争夺的巨大市场。<br> 主要有：</p><ul><li>电话网拨号接入</li><li>数字用户线接入(PSL)</li><li>光纤同轴混合网接入(HFC)</li><li>光纤接入(FTT)</li><li>以太网接入和无线接入</li></ul><h3 id="_2-电话网拨号接入" tabindex="-1">2.电话网拨号接入 <a class="header-anchor" href="#_2-电话网拨号接入" aria-label="Permalink to &quot;2.电话网拨号接入&quot;">​</a></h3><p>早期住宅用户接入互联网的方式，简单易行、成本低(<span class="green">非宽带接入</span>)<br> 通过拨号调制解调器接入</p><ul><li>允许最高56Kbps接入速率(通过会更低)</li><li>不能同时上网和打电话，不提供持续连接</li></ul><h3 id="_3-adsl技术" tabindex="-1">3.ADSL技术 <a class="header-anchor" href="#_3-adsl技术" aria-label="Permalink to &quot;3.ADSL技术&quot;">​</a></h3><p><span class="green">非对称数字用户线ADSL(Asymmetric Digital Subscriber Line)</span>技术：用数字技术对现有的模拟电话用户线进行改造，使它能改承载宽带业务。</p><ul><li>ADSL技术把 0 ~ 4kHz 低端频谱留给传统电话使用，而把原来没有被用的高端频谱留给用户上网使用。</li><li>采用<span class="green">离散多音调DMT(Discrete Multi-Tone)</span>调制技术。</li><li>DMT调制技术采用<span class="green">频分复用FDM</span>方法。下行带宽远大于上行带宽。<br></li></ul>',10)),e(i,{src:"/blog/images/study/ne/DMT技术的频谱分布.png",title:"DMT技术的频谱分布"}),l[1]||(l[1]=a("br",null,null,-1)),l[2]||(l[2]=a("h4",{id:"adsl的组成",tabindex:"-1"},[s("ADSL的组成 "),a("a",{class:"header-anchor",href:"#adsl的组成","aria-label":'Permalink to "ADSL的组成"'},"​")],-1)),e(i,{src:"/blog/images/study/ne/ADSL的组成.png",title:"ADSL的组成"}),l[3]||(l[3]=a("br",null,null,-1)),l[4]||(l[4]=a("h3",{id:"_4-光纤同轴混合物-hfc网",tabindex:"-1"},[s("4.光纤同轴混合物(HFC网) "),a("a",{class:"header-anchor",href:"#_4-光纤同轴混合物-hfc网","aria-label":'Permalink to "4.光纤同轴混合物(HFC网)"'},"​")],-1)),l[5]||(l[5]=a("p",null,[s("HFC(Hybrid Fiber Coax)网基于有线电视网CATV网。"),a("br")],-1)),l[6]||(l[6]=a("ul",null,[a("li",null,"改造：把原有线电视网中的同轴电缆主干部分改换为光纤"),a("li",null,[s("采用"),a("span",{class:"green"},"频分复用FDM"),s("方法")])],-1)),e(i,{src:"/blog/images/study/ne/HFC网的结构.png",title:"HFC网的结构"}),l[7]||(l[7]=a("br",null,null,-1)),l[8]||(l[8]=a("ul",null,[a("li",null,"原来的CATV网的最高传输频率是450MHz，并且仅用于电视信号的下行传输。"),a("li",null,"HFC网具有双向传输功能，而且扩展了传输频带。根据有线电视频率配置标准。")],-1)),e(i,{src:"/blog/images/study/ne/我国HFC网的频谱划分.png",title:"我国HFC网的频谱划分"}),l[9]||(l[9]=n('<br><p>所需设备：</p><ul><li>机顶盒(set-top box):</li><li><ul><li>连接在同轴电缆和用户的电视机之间。</li></ul></li><li><ul><li>使现有的模拟电视机能够接收数字电视信号。</li></ul></li><li>电缆调制解调器(cable modem):</li><li><ul><li>将用户计算机接入互联网。</li></ul></li><li><ul><li>在上行信道中传送交互数字电视所需的一些信息。</li></ul></li><li><ul><li>不需要成对使用，而只需安装在用户端。</li></ul></li><li><ul><li>复杂，必须解决共享信道中可能出现的冲突问题。</li></ul></li></ul><h3 id="_5-光纤接入fttx技术-目前正在使用" tabindex="-1">5.光纤接入FTTx技术(目前正在使用) <a class="header-anchor" href="#_5-光纤接入fttx技术-目前正在使用" aria-label="Permalink to &quot;5.光纤接入FTTx技术(目前正在使用)&quot;">​</a></h3><p><span class="green">FTTx</span>表示Fiber To The...(光纤到)，例如：</p><ul><li>光纤到房间<span class="brown">FTTR</span>(Fiber To The Room)</li><li>光纤到户<span class="brown">FTTH</span>(Fiber To The Home)</li><li>光纤到大楼<span class="brown">FTTB</span>(Fiber To The Building)</li><li>光纤到路边<span class="brown">FTTC</span>(Fiber To The Curb)</li><li>光纤到小区<span class="brown">FTTZ</span>(Fiber To The Zone)</li><li>光纤到办公室<span class="brown">FTTO</span>(Fiber To The Office)</li><li>光纤到桌面<span class="brown">FTTD</span>(Fiber To The Desk)</li><li>等。</li></ul><h4 id="无源光网络pon-passive-optical-network" tabindex="-1">无源光网络PON(Passive Optical Network) <a class="header-anchor" href="#无源光网络pon-passive-optical-network" aria-label="Permalink to &quot;无源光网络PON(Passive Optical Network)&quot;">​</a></h4><ul><li>采用<span class="green">波分复用WDM</span></li><li>下行波长<span class="brown">1490nm</span>采用<span class="brown">广播</span>技术</li><li>上行波长<span class="brown">1310nm</span>采用<span class="brown">时分复用TDMA</span>技术</li></ul>',8)),e(i,{src:"/blog/images/study/ne/无源光网络PON.png",title:"无源光网络PON"}),l[10]||(l[10]=n('<br><h4 id="两种常用类型" tabindex="-1">两种常用类型 <a class="header-anchor" href="#两种常用类型" aria-label="Permalink to &quot;两种常用类型&quot;">​</a></h4><ul><li><span class="green">以太网无源光网络EPON(Ethernet PON)</span></li><li><ul><li>在链路层使用以太网协议，利用PON的拓扑结构实现以太网的接入。</li></ul></li><li><ul><li>与现有以太网的兼容性好，并且成本低，扩展性强，管理方便。</li></ul></li><li><span class="green">吉比特无源光网络GPON(Gigabit PON)</span></li><li><ul><li>采用通用封装方法GEM(Generic Encapsulation Method)，可承载多业务，且对各种业务类型都能够提供服务质量保证，总体性能比EPON好。</li></ul></li><li><ul><li>成本稍高。</li></ul></li></ul>',3))])}const P=r(u,[["render",T]]);export{g as __pageData,P as default};
