import{_ as r,C as n,c as o,o as u,b1 as e,G as s,j as a,a as t}from"./chunks/framework.w2ptJf3g.js";const b=JSON.parse('{"title":"二.6.以太网MAC层","description":"","frontmatter":{},"headers":[],"relativePath":"views/study/ne/theory/二/6.以太网MAC层.md","filePath":"views/study/ne/theory/二/6.以太网MAC层.md","lastUpdated":1752743746000}'),d={name:"views/study/ne/theory/二/6.以太网MAC层.md"};function _(c,l,p,A,m,C){const i=n("ImageViewer");return u(),o("div",null,[l[0]||(l[0]=e('<h1 id="二-6-以太网mac层" tabindex="-1">二.6.以太网MAC层 <a class="header-anchor" href="#二-6-以太网mac层" aria-label="Permalink to &quot;二.6.以太网MAC层&quot;">​</a></h1><h3 id="_1-mac" tabindex="-1">1.MAC <a class="header-anchor" href="#_1-mac" aria-label="Permalink to &quot;1.MAC&quot;">​</a></h3><h4 id="_1-mac层的硬件地址" tabindex="-1">1.MAC层的硬件地址 <a class="header-anchor" href="#_1-mac层的硬件地址" aria-label="Permalink to &quot;1.MAC层的硬件地址&quot;">​</a></h4><ul><li>MAC(Media Access Control)媒体访问控制</li><li>硬件地址又称为<span class="green">物理地址</span>，或MAC地址</li><li>IEEE 802标准为局域网规定了一种<span class="green">48位</span>的全球地址(简称为地址)，是指局域网上的每一台计算机中固话在适配器的ROM中的地址</li></ul>',4)),s(i,{src:"/blog/images/study/ne/MAC地址.png",title:"MAC地址"}),l[1]||(l[1]=e('<br><h4 id="_2-mac地址特殊位含义" tabindex="-1">2.MAC地址特殊位含义 <a class="header-anchor" href="#_2-mac地址特殊位含义" aria-label="Permalink to &quot;2.MAC地址特殊位含义&quot;">​</a></h4><ul><li>IEEE 规定地址字段的第1字节的最低位为 I/G (Individual / Group)位</li><li><ul><li>单站地址：I/G 位 = 0</li></ul></li><li><ul><li>组地址：I/G 位 = 1。组地址用来进行多播。</li></ul></li><li><ul><li>广播地址：所有48位都为1(全1)。只能作为目的地址使用。</li></ul></li><li>IEEE 把地址字段第1字节的倒数第2位规定为 G/L (Global / Local)位</li><li><ul><li>全球管理： G/L 位 = 1。厂商向IEEE购买的OUI都属于全球管理。</li></ul></li><li><ul><li>本地管理： G/L 位 = 0。这时用户可任意分配网络上的地址。</li></ul></li></ul><p>比如：F8-A2-D6-D2-BD-7D<br> 111110<span class="red">0</span><span class="brown">0</span> - 10100010 - 11010110 - 11010010 - 10111101 - 01111101</p><h4 id="_3-适配器" tabindex="-1">3.适配器 <a class="header-anchor" href="#_3-适配器" aria-label="Permalink to &quot;3.适配器&quot;">​</a></h4><ul><li>适配器具有过滤功能</li><li>每收到一个MAC帧，先用硬件检查帧中的MAC地址</li><li>如果是发往本站的帧则收下，然后再进行其他的处理</li><li>否则就将此帧丢弃，不再进行其他的处理</li></ul><p>&quot;发往本站的帧&quot;包括以下3种帧：</p><ul><li>单播(unicast) 帧 (一对一)</li><li>广播(broadcast) 帧 (一对全体)</li><li>多播(multicast) 帧 (一对多) <br></li></ul><p>以<span class="red">混杂方式(promiscuous mode)</span>工作的以太网适配器只要&quot;听到&quot;有帧在以太网上传输就<span class="red">都接收</span>下来</p><h3 id="_2-mac帧的格式" tabindex="-1">2.MAC帧的格式 <a class="header-anchor" href="#_2-mac帧的格式" aria-label="Permalink to &quot;2.MAC帧的格式&quot;">​</a></h3><p>常用的以太网MAC帧格式有2种标准</p><ul><li>1.DIX Ethernet V2标准</li><li>2.IEEE 的802.3标准 <br></li></ul><p>最常用的MAC帧格式是<span class="green">以太网V2</span>的格式<br> 只有少数协议使用802.3帧封装二层：<span class="brown">STP、GVRP、IS-IS</span>等</p><h4 id="以太网-v-2的-mac-帧格式" tabindex="-1">以太网 V 2的 MAC 帧格式 <a class="header-anchor" href="#以太网-v-2的-mac-帧格式" aria-label="Permalink to &quot;以太网 V 2的 MAC 帧格式&quot;">​</a></h4>',14)),s(i,{src:"/blog/images/study/ne/以太网V2的MAC帧格式.png",title:"以太网V2的MAC帧格式"}),l[2]||(l[2]=a("br",null,null,-1)),l[3]||(l[3]=a("p",null,[t("当数据字段的长度小于46字节时，应在数据字段的后面加入整数字节的"),a("span",{class:"brown"},"填充字段"),t("，以保证以太网的MAC帧长不小于64字节")],-1)),l[4]||(l[4]=a("h4",{id:"ieee802-3与以太网v2帧格式的区别",tabindex:"-1"},[t("IEEE802.3与以太网V2帧格式的区别 "),a("a",{class:"header-anchor",href:"#ieee802-3与以太网v2帧格式的区别","aria-label":'Permalink to "IEEE802.3与以太网V2帧格式的区别"'},"​")],-1)),s(i,{src:"/blog/images/study/ne/IEEE802.3与以太网V2帧格式的区别.png",title:"IEEE802.3与以太网V2帧格式的区别"}),l[5]||(l[5]=e('<br><ul><li>IPv4：0x0800</li><li>ARP：0x0806</li><li>802.1Q tag：0x8100</li><li>IPv6：0x86DD</li><li>PPPoE：0x8863/0x8864</li><li>MPLS Label：0x8847/0x8848</li></ul><h4 id="无效的mac帧" tabindex="-1">无效的MAC帧 <a class="header-anchor" href="#无效的mac帧" aria-label="Permalink to &quot;无效的MAC帧&quot;">​</a></h4><ul><li>数据字段的长度与长度字段的值不一致</li><li>帧的长度不是整数个字节</li><li>用收到的帧检验序列FCS查出有差错</li><li>数据字段的长度不在46 ~ 1500字节之间</li><li><ul><li>对于检查出的无效MAC帧就简单的丢弃</li></ul></li><li><ul><li>以太网不负责重传丢弃的帧</li></ul></li></ul>',4))])}const M=r(d,[["render",_]]);