import{_ as r,C as o,c as p,o as t,j as a,G as i,a4 as n,a as e}from"./chunks/framework.C_GGjjHE.js";const P=JSON.parse('{"title":"五.1.DNS协议","description":"","frontmatter":{},"headers":[],"relativePath":"views/study/ne/theory/五/1.DNS协议.md","filePath":"views/study/ne/theory/五/1.DNS协议.md","lastUpdated":1753783179000}'),u={name:"views/study/ne/theory/五/1.DNS协议.md"};function c(d,l,_,h,b,S){const s=o("ImageViewer");return t(),p("div",null,[l[0]||(l[0]=a("h1",{id:"五-1-dns协议",tabindex:"-1"},[e("五.1.DNS协议 "),a("a",{class:"header-anchor",href:"#五-1-dns协议","aria-label":'Permalink to "五.1.DNS协议"'},"​")],-1)),l[1]||(l[1]=a("h3",{id:"_1-互联网的域名结构",tabindex:"-1"},[e("1.互联网的域名结构 "),a("a",{class:"header-anchor",href:"#_1-互联网的域名结构","aria-label":'Permalink to "1.互联网的域名结构"'},"​")],-1)),i(s,{src:"/blog/images/study/ne/互联网的域名结构.png",title:"互联网的域名结构"}),l[2]||(l[2]=n('<br><ul><li>域名结构：<span class="green">层次结构</span>。由标号(label)序列组成，各标号之间用点(.)隔开，各标号分别代表不同级别的域名</li><li>三级域名</li><li><ul><li>不区分大小写，级别最低</li></ul></li><li>二级域名</li><li><ul><li>每个标号不超过63个字符</li></ul></li><li>顶级域名</li><li><ul><li>完整域名总共不超过255个字符，级别最高</li></ul></li></ul><h3 id="_2-域名系统" tabindex="-1">2.域名系统 <a class="header-anchor" href="#_2-域名系统" aria-label="Permalink to &quot;2.域名系统&quot;">​</a></h3>',3)),i(s,{src:"/blog/images/study/ne/域名系统DNS.png",title:"域名系统DNS"}),l[3]||(l[3]=n('<br><ul><li><span class="green">域名系统DNS(Domain Name System)</span></li><li><ul><li>互联网使用的命名系统</li></ul></li><li><ul><li>用来把人们使用的机器<span class="brown">名字(域名)转换为IP地址</span></li></ul></li><li><ul><li>为互联网的各种网络应用提供了核心服务</li></ul></li><li><span class="green">域名</span>采用层次<span class="brown">树状结构</span>的命名方法</li><li><ul><li>DNS是一个联机<span class="brown">分布式数据库系统</span>，采用<span class="brown">客户服务器</span>方式</li></ul></li><li><ul><li>域名到IP地址的解析是由若干个<span class="brown">域名服务器程序</span>共同完成</li></ul></li><li><ul><li>域名服务器程序在专设的结点上运行，运行该程序的机器称为<span class="brown">域名服务器</span></li></ul></li></ul><h3 id="_3-域名服务器类型" tabindex="-1">3.域名服务器类型 <a class="header-anchor" href="#_3-域名服务器类型" aria-label="Permalink to &quot;3.域名服务器类型&quot;">​</a></h3><ul><li>1.根域名服务器</li><li>2.顶级域名服务器</li><li>3.权限域名服务器</li><li>4.本地域名服务器</li></ul><h4 id="_1-根域名服务器" tabindex="-1">1.根域名服务器 <a class="header-anchor" href="#_1-根域名服务器" aria-label="Permalink to &quot;1.根域名服务器&quot;">​</a></h4><ul><li><span class="green">最高层次，最为重要</span></li><li>所有根域名服务器都<span class="brown">知道</span>所有的<span class="brown">顶级域名服务器</span>的域名和IP地址</li><li>不管是哪一个本地域名服务器，若要对互联网上任何一个域名进行解析，只要自己无法解析，就<span class="brown">首先</span>求助于根域名服务器</li><li>若所有的根域名服务器都瘫痪了，则整个互联网的DNS系统就无法正常工作</li></ul><h4 id="_2-顶级域名服务器" tabindex="-1">2.顶级域名服务器 <a class="header-anchor" href="#_2-顶级域名服务器" aria-label="Permalink to &quot;2.顶级域名服务器&quot;">​</a></h4><ul><li>顶级域名服务器(即<span class="green">TLD服务器</span>)负责管理在该顶级域名服务器注册的<span class="green">所有二级域名</span></li><li>当收到DNS查询请求时，就给出相应的回答(可能是最后的结果，也可能是下一步应当找的域名服务器的IP地址)</li></ul><h4 id="_3-权限域名服务器" tabindex="-1">3.权限域名服务器 <a class="header-anchor" href="#_3-权限域名服务器" aria-label="Permalink to &quot;3.权限域名服务器&quot;">​</a></h4><ul><li>负责一个区(zone)的域名服务器</li><li>当一个权限域名服务器还不能给出最后的查询回答时，就会告诉发出查询请求的DNS客户，下一步应当找哪一个权限域名服务器</li></ul><h4 id="_4-本地域名服务器" tabindex="-1">4.本地域名服务器 <a class="header-anchor" href="#_4-本地域名服务器" aria-label="Permalink to &quot;4.本地域名服务器&quot;">​</a></h4><ul><li><span class="green">非常重要</span></li><li>当一个主机发出DNS查询请求时，该查询请求报文就<span class="brown">发送</span>给本地域名服务器</li><li>每一个互联网服务提供者ISP或一个大学，都可以拥有一个本地域名服务器</li><li>当所要查询的主机也属于同一个本地ISP时，该本地域名服务器立即就能将所查询的主机名转换为它的IP地址，而<span class="brown">不需要</span>再去询问其他的域名服务器</li><li>本地域名服务器有时也称为<span class="brown">默认域名服务器</span></li></ul><h4 id="_5-提高域名服务器的可靠性" tabindex="-1">5.提高域名服务器的可靠性 <a class="header-anchor" href="#_5-提高域名服务器的可靠性" aria-label="Permalink to &quot;5.提高域名服务器的可靠性&quot;">​</a></h4><ul><li>DNS域名服务器都把数据复制到几个域名服务器来保存，其中的一个是<span class="green">主域名服务器</span>，其他的是<span class="green">辅助域名服务器</span></li><li>当主域名服务器出现故障时，辅助域名服务器可以保证DNS的查询工作不会中断</li><li>主域名服务器<span class="brown">定期</span>把数据复制到辅助域名服务器中，而<span class="brown">更改</span>数据<span class="brown">只能</span>在主域名服务器中进行，保证了数据的一致性</li></ul><h3 id="_4-域名的解析过程" tabindex="-1">4.域名的解析过程 <a class="header-anchor" href="#_4-域名的解析过程" aria-label="Permalink to &quot;4.域名的解析过程&quot;">​</a></h3><ul><li>递归查询</li><li><ul><li>通常，主机向本地域名服务器查询时使用</li></ul></li><li><ul><li>若不知道，就以DNS客户的身份，向其他根域名服务器继续发出查询请求报文</li></ul></li><li>迭代查询</li><li><ul><li>本地域名服务器向根域名服务器查询时使用</li></ul></li><li><ul><li>要么给出所要查询的IP地址，要么告诉下一个要查询的域名服务器的IP地址</li></ul></li><li><ul><li>本地域名服务器继续后续查询</li></ul></li></ul><h4 id="dns域名一般解析过程" tabindex="-1">DNS域名一般解析过程 <a class="header-anchor" href="#dns域名一般解析过程" aria-label="Permalink to &quot;DNS域名一般解析过程&quot;">​</a></h4>',17)),i(s,{src:"/blog/images/study/ne/DNS域名一般解析过程.png",title:"DNS域名一般解析过程"}),l[4]||(l[4]=n('<br><h3 id="_5-dns常见解析记录" tabindex="-1">5.DNS常见解析记录 <a class="header-anchor" href="#_5-dns常见解析记录" aria-label="Permalink to &quot;5.DNS常见解析记录&quot;">​</a></h3><ul><li><span class="green">A 记录</span>: A（Address）记录是用来指定主机名（或域名）对应的 <span class="brown">IP 地址记录</span>。</li><li><span class="green">CNAME 记录</span>：通常称别名解析，是主机名到主机名的映射。当需要将<span class="brown">域名指向另一个域名</span>，再由另一个域名提供 IP 地址，就需要添加 CNAME 记录。</li><li><span class="green">NS 记录</span>：域名服务器记录，如果需要把域名交给<span class="brown">其他 DNS 服务器解析</span>，就需要添加 NS 记录（Name Server）。</li><li><span class="green">AAAA 记录</span>:（AAAA record）是用来将域名解析到 <span class="brown">IPv6 地址的 DNS 记录</span>。</li><li><span class="green">MX 记录</span>:（Mail Exchanger）记录是<span class="brown">邮件交换记录</span>，主要用于邮箱解析。</li><li><span class="green">PTR 记录</span>: PTR 是 pointer 的简写，即 <span class="brown">“反向 DNS”</span>，用于将一个 IP 地址映射到对应的主机名，即通过 IP 访问域名。</li><li><span class="green">SRV 记录</span>：服务定位（SRV）资源记录，用于定义<span class="brown">提供特定服务的服务器</span>的位置，如主机，端口等。</li><li><span class="green">SOA 记录</span>：是<span class="brown">起始授权机构记录</span>，说明了在众多 NS 记录里哪一台才是主要的服务器。</li><li><span class="green">TXT 记录</span>：一般指某个<span class="brown">主机名或域名的标识和说明</span>。</li></ul>',3))])}const D=r(u,[["render",c]]);export{P as __pageData,D as default};
