import{_ as t,C as n,c as r,o,a4 as i,G as s,j as a,a as p}from"./chunks/framework.C_GGjjHE.js";const F=JSON.parse('{"title":"五.3.FTP协议","description":"","frontmatter":{},"headers":[],"relativePath":"views/study/ne/theory/五/3.FTP协议.md","filePath":"views/study/ne/theory/五/3.FTP协议.md","lastUpdated":1753862375000}'),u={name:"views/study/ne/theory/五/3.FTP协议.md"};function d(_,l,T,P,c,h){const e=n("ImageViewer");return o(),r("div",null,[l[0]||(l[0]=i('<h1 id="五-3-ftp协议" tabindex="-1">五.3.FTP协议 <a class="header-anchor" href="#五-3-ftp协议" aria-label="Permalink to &quot;五.3.FTP协议&quot;">​</a></h1><h3 id="_1-概述" tabindex="-1">1.概述 <a class="header-anchor" href="#_1-概述" aria-label="Permalink to &quot;1.概述&quot;">​</a></h3><ul><li><span class="green">文件传送协议FTP(File Transfer Protocol)</span>是互联网上使用得最广泛的文件传送协议</li><li>提供了<span class="green">交互式</span>的访问，允许客户指明文件的类型与格式，并允许文件具有储存权限</li><li>屏蔽了各计算机系统的细节，因而适合于在异构网络中任意计算机之间传送文件</li><li>是<span class="green">文件共享协议</span>的一个大类</li><li><ul><li>文件传送协议：<span class="brown">FTP、SFTP、TFTP</span>等</li></ul></li><li><ul><li>联机访问(on-line access)协议：<span class="brown">NFS、CIFS</span>等</li></ul></li></ul><h3 id="_2-ftp工作原理" tabindex="-1">2.FTP工作原理 <a class="header-anchor" href="#_2-ftp工作原理" aria-label="Permalink to &quot;2.FTP工作原理&quot;">​</a></h3><ul><li>FTP工作在应用层，使用<span class="green">TCP</span>传输</li><li>FTP是一个多通道协议，需要建立两个TCP连接(<span class="brown">控制连接、数据连接</span>)才能完成文件的传输</li><li>FTP两种工作模式：<span class="brown">主动模式、被动模式</span></li><li>FTP两种数据传输方式：<span class="brown">ASCII、Binary</span></li></ul><h4 id="_1-主动模式" tabindex="-1">1.主动模式 <a class="header-anchor" href="#_1-主动模式" aria-label="Permalink to &quot;1.主动模式&quot;">​</a></h4>',6)),s(e,{src:"/blog/images/study/ne/FTP主动模式.png",title:"FTP主动模式"}),l[1]||(l[1]=a("br",null,null,-1)),l[2]||(l[2]=a("h4",{id:"_2-被动模式",tabindex:"-1"},[p("2.被动模式 "),a("a",{class:"header-anchor",href:"#_2-被动模式","aria-label":'Permalink to "2.被动模式"'},"​")],-1)),s(e,{src:"/blog/images/study/ne/FTP被动模式.png",title:"FTP被动模式"}),l[3]||(l[3]=i('<br><h3 id="_3-ftp客户端常用命令-windows" tabindex="-1">3.FTP客户端常用命令(Windows) <a class="header-anchor" href="#_3-ftp客户端常用命令-windows" aria-label="Permalink to &quot;3.FTP客户端常用命令(Windows)&quot;">​</a></h3><ul><li>1.登录命令：ftp [主机名/IP]</li><li><ul><li>示例：ftp example.com</li></ul></li><li>2.上传文件 put [本地文件路径] [远程文件路径]</li><li><ul><li>示例：put C:\\example.txt /example.txt</li></ul></li><li>3.下载文件：get [远程文件路径] [本地文件路径]</li><li><ul><li>示例：get /example.txt C:\\example.txt</li></ul></li><li>4.删除文件：delete [远程文件路径]</li><li><ul><li>示例：delete /example.txt</li></ul></li><li>5.列出目录：ls [远程目录路径]</li><li><ul><li>示例：ls /user/local</li></ul></li><li>6.改变目录：cd [远程目录路径]</li><li><ul><li>示例：cd /user/local</li></ul></li><li>7.退出FTP：quit</li><li>8.二进制传输模式：binary</li><li>9.ASCII传输模式：ascii</li><li>10.终止当前FTP会话：close</li></ul>',3))])}const f=t(u,[["render",d]]);export{F as __pageData,f as default};
